package com.sheepclass.entity;import java.util.Date;/** * <p>Title: </p> * <p>Description: </p> *  *  * @date 2019-04-09 */ public class Chapter {	private float serialnum;	private int courseid;	private String chaptername;	private String vedio;	private String sets;	public float getSerialnum() {		return serialnum;	}	public void setSerialnum(float serialnum) {		String r  = String.format("%.1f",serialnum);		this.serialnum = Float.parseFloat(r);	}	public int getCourseid() {		return courseid;	}	public void setCourseid(int courseid) {		this.courseid = courseid;	}	public String getChaptername() {		return chaptername;	}	public void setChaptername(String chaptername) {		this.chaptername = chaptername;	}	public String getVedio() {		return vedio;	}	public void setVedio(String vedio) {		this.vedio = vedio;	}	public String getSets() {		return sets;	}	public void setSets(String sets) {		this.sets = sets;	}}